const formatDate = (dateString) => {
  const options = { year: "numeric", month: "long", day: "numeric" };
  return new Date(dateString).toLocaleDateString("ko-KR", options);
};

const Header = ({ dateId }) => {
  const formattedDate = formatDate(dateId);

  return (
    <div className={styles.Header}>
