import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import styles from "./Home.module.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    const formattedDate = new Date(date.getTime() + 86400000)
      .toISOString()
      .substr(0, 10); // ÌÅ¥Î¶≠Ìïú ÎÇ†ÏßúÎ°ú Î∞îÎ°ú Ï≤òÎ¶¨
    const month = formattedDate.getMonth() + 1; // 0Î∂ÄÌÑ∞ ÏãúÏûëÌïòÎØÄÎ°ú +1
    const day = formattedDate.getDate();
    setSelectedDate(date);
    navigate(`/TodoPage/${formattedDate}`);
  };

  const handleLogout = () => {
    const userId = localStorage.getItem("userId");

    axios
      .get(`http://toodoolist.shop/api/users/${userId}/log-out`)
      .then((response) => {
        console.log(response.data);
        navigate("/");
      })
      .catch((error) => {
        console.error("ÏóêÎü¨ Î∞úÏÉù:", error);
      });
  };

  return (
    <div className={styles.Home}>
      <h2 className={styles.title}>Ï∫òÎ¶∞ÎçîüìÜ</h2>
      <Calendar
        onChange={handleDateChange}
        value={selectedDate}
        className={styles.calendar}
        locale="en-US" // ÎØ∏Íµ≠Ïãù Î°úÏºÄÏùº ÏÇ¨Ïö©
      />
      <button className={styles.log_out} onClick={handleLogout}>
        Î°úÍ∑∏ÏïÑÏõÉ
      </button>
    </div>
  );
};

export default Home;
