import styles from "./SignUp.module.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const SignUp = () => {
  const navigate = useNavigate();
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [responseData, setResponseData] = useState(null);

  // ÏïÑÏù¥Îîî, ÎπÑÎ∞ÄÎ≤àÌò∏ 4Ïûê ÎØ∏Îßå ÏÇΩÏûÖÏãú Í≤ΩÍ≥†
  const wordCondition = () => {
    if (id.length < 4 || pw.length < 4) {
      alert("4Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");
      return false;
    }
    return true;
  };

  const handlePostRequest = () => {
    const url = "/api/users/sign-up";
    const data = {
      username: id,
      password: pw,
    };

    axios
      .post(url, data)
      .then((response) => {
        console.log("ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ:", response.data); // ÏÑúÎ≤Ñ ÏùëÎãµ ÌôïÏù∏
        setResponseData(response.data);
        // ÏÑ±Í≥µÌïú Í≤ΩÏö∞ÏóêÎßå ÌöåÏõêÍ∞ÄÏûÖ Î©îÏãúÏßÄÏôÄ Î¶¨Îã§Ïù¥Î†âÌä∏ Ìò∏Ï∂ú
        alert("ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å! Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏ÏöîüôÇ");
        navigate("/");
      })
      .catch((error) => {
        console.error("ÏóêÎü¨ Î∞úÏÉù:", error);
      });
  };

  return (
    <div className={styles.SignUp}>
      <h2 className={styles.title}>ÌöåÏõêÍ∞ÄÏûÖ</h2>
      <div className={styles.input_box}>
        <div className={styles.id_box}>
          <h4>ÏïÑÏù¥Îîî</h4>
          <input
            className={styles.ID}
            type="email"
            placeholder="4Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            value={id} // id Í∞í ÏóÖÎç∞Ïù¥Ìä∏
            onChange={(e) => setId(e.target.value)} // id Í∞íÏùÑ Î≥ÄÍ≤ΩÌï† ÎïåÎßàÎã§ ÏóÖÎç∞Ïù¥Ìä∏
          />
        </div>
        <div className={styles.pw_box}>
          <h4>ÎπÑÎ∞ÄÎ≤àÌò∏</h4>
          <input
            className={styles.PW}
            type="password"
            placeholder="4Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            value={pw} // pw Í∞í ÏóÖÎç∞Ïù¥Ìä∏
            onChange={(e) => setPw(e.target.value)} // pw Í∞íÏùÑ Î≥ÄÍ≤ΩÌï† ÎïåÎßàÎã§ ÏóÖÎç∞Ïù¥Ìä∏
          />
        </div>
        <button onClick={() => wordCondition() && handlePostRequest()}>
          Í∞ÄÏûÖÌïòÍ∏∞
        </button>
      </div>
    </div>
  );
};

export default SignUp;
