import "./TodoList.css";
import TodoItem from "./TodoItem";
import { useState } from "react";

const TodoList = ({ todo, onUpdate, onDelete }) => {
  const [search, setSearch] = useState("");
  const onChangeSearch = (e) => {
    setSearch(e.target.value);
  };
  let style1 = { color: "black" };
  let style2 = { color: "gray", textDecorationLine: "line-through" };

  // Í≤ÄÏÉâÏñ¥Ïóê Îî∞Îùº ÌïÑÌÑ∞ÎßÅÌïòÍ∏∞ & ÎåÄÏÜå Î¨∏Ïûê Íµ¨Î≥Ñ X (Ï†ÑÎ∂Ä ÏÜåÎ¨∏ÏûêÎ°ú)
  const getSearchResults = () => {
    return search === ""
      ? todo
      : todo.filter((it) =>
          it.content.toLowerCase().includes(search.toLowerCase())
        );
  };

  return (
    <div className="TodoList">
      <h4>Todo List üìò</h4>
      <input
        value={search}
        onChange={onChangeSearch}
        className="searchbar"
        placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
      />
      <div className="list_wrapper">
        {getSearchResults().map((it) => (
          <TodoItem
            key={it.id}
            {...it}
            onUpdate={onUpdate}
            onDelete={onDelete}
          />
          // {...it} : Ïª¥Ìè¨ÎÑåÌä∏Ïóê it Í∞ùÏ≤¥ Í∞ÅÍ∞ÅÏùò ÌîÑÎ°úÌçºÌã∞ Ï†ÑÎã¨
        ))}
      </div>
    </div>
  );
};
export default TodoList;
