import "./App.css";
import Header from "./component/Header";
import TodoEditor from "./component/TodoEditor";
import Todolist from "./component/Todolist";
import { useState } from "react";

const mockTodo = [
  {
    id: 0,
    isDone: false,
    content: "개발 공부하기",
    createdDate: new Date().getTime(),
  },
  {
    id: 1,
    isDone: false,
    content: "영어 공부하기",
    createdDate: new Date().getTime(),
  },
  {
    id: 2,
    isDone: false,
    content: "독서하기",
    createdDate: new Date().getTime(),
  },
  {
    id: 3,
    isDone: false,
    content: "운동하기",
    createdDate: new Date().getTime(),
  },
];

function App() {
  const idRef = useRef(3);
  const [todo, setTodo] = useState(mockTodo);

  const onCreate = (content) => {
    const newItem = {
      id: idRef.current,
      content,
      isDone: false,
      createdDate: new Date().getTime(),
    };
    setTodo([newItem, ...todo]);
  };
  return (
    <div className="App">
      <Header />
      <TodoEditor />
      <Todolist />
    </div>
  );
}

export default App;
