import styles from "./TodoList.module.css";
import TodoItem from "./TodoItem";
import { useState } from "react";

const TodoList = ({ todo, onCheck, onDelete, onEdit, onReview }) => {
  const [search, setSearch] = useState("");
  const onChangeSearch = (e) => {
    setSearch(e.target.value);
  };

  // Í≤ÄÏÉâÏñ¥Ïóê Îî∞Îùº ÌïÑÌÑ∞ÎßÅÌïòÍ∏∞ & ÎåÄÏÜå Î¨∏Ïûê Íµ¨Î≥Ñ X (Ï†ÑÎ∂Ä ÏÜåÎ¨∏ÏûêÎ°ú)
  const getSearchResults = () => {
    return search === ""
      ? todo
      : todo.filter((it) =>
          it.content.toLowerCase().includes(search.toLowerCase())
        );
  };

  return (
    <div className={styles.TodoList}>
      <h4>Todo List üìò</h4>
      <input
        value={search}
        onChange={onChangeSearch}
        className={styles.searchbar}
        placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
      />
      <div className={styles.list_wrapper}>
        {getSearchResults().map((it) => (
          <TodoItem
            key={it.plan_id}
            {...it}
            onCheck={onCheck}
            onDelete={onDelete}
            onEdit={onEdit}
            onReview={onReview}
          />
          // {...it} : Ïª¥Ìè¨ÎÑåÌä∏Ïóê it Í∞ùÏ≤¥ Í∞ÅÍ∞ÅÏùò ÌîÑÎ°úÌçºÌã∞ Ï†ÑÎã¨
        ))}
      </div>
    </div>
  );
};
export default TodoList;
