import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import styles from "./Home.module.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    const isoDateString = date.toISOString(); // ISO 8601 ÌòïÏãùÏùò Î¨∏ÏûêÏó¥ÏùÑ ÏñªÏäµÎãàÎã§.
    date.setDate(date.getDate() + 1); // ÎÇ†ÏßúÎ•º ÌïòÎ£® Îí§Î°ú Ïù¥ÎèôÌï©ÎãàÎã§.
    const formattedDate = date.toISOString().split("T")[0]; // Ïó∞, Ïõî, Ïùº Î∂ÄÎ∂ÑÎßå Ï∂îÏ∂úÌï©ÎãàÎã§.

    const month = date.getMonth() + 1; // 0Î∂ÄÌÑ∞ ÏãúÏûëÌïòÎØÄÎ°ú +1
    const day = date.getDate() - 1;
    localStorage.setItem("month", String(month));
    localStorage.setItem("day", String(day));
    setSelectedDate(date);
    navigate(`/todoPage/${formattedDate}`);
  };

  const handleLogout = () => {
    const userId = localStorage.getItem("userId");

    axios
      .get(`/api/users/${userId}/log-out`)
      .then((response) => {
        console.log(response.data);
        navigate("/");
      })
      .catch((error) => {
        console.error("ÏóêÎü¨ Î∞úÏÉù:", error);
      });
  };

  return (
    <div className={styles.Home}>
      <h2 className={styles.title}>Ï∫òÎ¶∞ÎçîüìÜ</h2>
      <Calendar
        onChange={handleDateChange}
        value={selectedDate}
        className={styles.calendar}
        locale="en-US" // ÎØ∏Íµ≠Ïãù Î°úÏºÄÏùº ÏÇ¨Ïö©
      />
      <button className={styles.log_out} onClick={handleLogout}>
        Î°úÍ∑∏ÏïÑÏõÉ
      </button>
    </div>
  );
};

export default Home;
