import Header from "../component/Header";
import TodoEditor from "../component/TodoEditor";
import TodoList from "../component/TodoList";
import styles from "./TodoPage.module.css";
import { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function TodoPage() {
  const { dateId } = useParams(); // url의 ":dateId" 부분에서 날짜 따옴
  const [todo, setTodo] = useState([]); // 일정 데이터

  // 아이템 추가 함수(일정 작성)
  const onCreate = async (content) => {
    try {
      const response = await axios.post(
        "http://toodoolist.shop/api/plans/{user_id}",
        {
          date: new Date().toISOString(), // ISO 8601 형식으로 변환
          content: content,
        }
      );

      setTodo([response.data, ...todo]);
    } catch (error) {
      console.error("POST 요청 실패:", error);
    }
  };

  // 아이템 체크 함수
  const onUpdate = (targetId) => {
    setTodo(
      todo.map((it) =>
        it.plan_id === targetId ? { ...it, is_checked: !it.is_checked } : it
      )
    );
  };

  // 아이템 삭제 함수
  const onDelete = (targetId) => {
    setTodo(todo.filter((it) => it.plan_id !== targetId));
  };

  // 아이템 수정 함수
  const onEdit = (targetId, newContent) => {
    setTodo(
      todo.map((it) =>
        it.plan_id === targetId ? { ...it, content: newContent } : it
      )
    );
  };

  return (
    <div className={styles.TodoPage}>
      <Header dateId={dateId} />
      <TodoEditor onCreate={onCreate} />
      <TodoList
        todo={todo}
        onUpdate={onUpdate}
        onDelete={onDelete}
        onEdit={onEdit}
      />
    </div>
  );
}

export default TodoPage;
